surfaceFn:
function surface(input) {
    const u = (input.u - 0.5) * 10;
    const v = (input.v - 0.5) * 10;
    const eps = input.epsilon || 0.0;

    const z = Math.sin(u)*Math.cos(v) * eps;

    return {
        x: u, 
        y: v, 
        z: z, 
        r: 0.5, 
        g: 0.5, 
        b: 0.5, 
        a: 0.8
    };
}
