function surface(input) {
    const R = 2;

    // Standard N-sphere parametrization angles
    // u, v provide the surface sweep (phi1, phi2)
    // phi3, phi4, phi5 slice the higher dimensions
    const phi1 = input.u;      // [0, PI]
    const phi2 = input.v;      // [0, PI]
    const phi3 = input.phi3 || 1.57;
    const phi4 = input.phi4 || 1.57;
    const phi5 = input.phi5 || 0;     // [0, 2PI]

    // Precompute sines/cosines
    const s1 = Math.sin(phi1), c1 = Math.cos(phi1);
    const s2 = Math.sin(phi2), c2 = Math.cos(phi2);
    const s3 = Math.sin(phi3), c3 = Math.cos(phi3);
    const s4 = Math.sin(phi4), c4 = Math.cos(phi4);
    const s5 = Math.sin(phi5), c5 = Math.cos(phi5);

    // 6D Coordinates definition
    // x1 = R cos(phi1)
    // x2 = R sin(phi1) cos(phi2)
    // x3 = R sin(phi1) sin(phi2) cos(phi3)
    // x4 = R sin(phi1) sin(phi2) sin(phi3) cos(phi4)
    // x5 = R sin(phi1) sin(phi2) sin(phi3) sin(phi4) cos(phi5)
    // x6 = R sin(phi1) sin(phi2) sin(phi3) sin(phi4) sin(phi5)

    const x1 = R * c1;
    const x2 = R * s1 * c2;
    const x3 = R * s1 * s2 * c3;
    const x4 = R * s1 * s2 * s3 * c4;
    const x5 = R * s1 * s2 * s3 * s4 * c5;
    const x6 = R * s1 * s2 * s3 * s4 * s5;

    // Map 6D coordinates to spatial (x,y,z) and color (r,g,b)
    // Spatial Position
    const x = x1;
    const y = x2;
    const z = x3;

    // Color Mapping
    // Map coordinates (roughly [-R, R]) to [0, 1] range for color
    const r = (x4 / R + 1) / 2;
    const g = (x5 / R + 1) / 2;
    const b = (x6 / R + 1) / 2;

    return {
        x, y, z,
        r, g, b,
        a: 1
    };
}
