function surface(input) {
    const u = input.u, v = input.v;
    const deg = Math.PI / 180;

    const alpha = input.alpha * deg;
    const beta = input.beta * deg;
    const phi = input.phi * deg;
    const omega = input.omega * deg;
    const mu = input.mu * deg;
    const sMinDeg = input.sMin, sMaxDeg = input.sMax;
    const A = input.A, a = input.a, b = input.b;
    const D = input.D >= 0 ? 1 : -1;
    const L = input.L, N = input.N, W1 = input.W1, W2 = input.W2, P = input.P * deg;
    const turns = input.turns;

    const theta = u * turns * 2 * Math.PI;
    const sDeg = sMinDeg + v * (sMaxDeg - sMinDeg);
    const s = sDeg * deg;

    const cosS = Math.cos(s), sinS = Math.sin(s);
    const Re = 1 / Math.sqrt((cosS*cosS)/(a*a) + (sinS*sinS)/(b*b));

    const thetaDeg = theta / deg;
    const fTheta = (360/N) * ((thetaDeg * N / 360) - Math.floor(thetaDeg * N / 360));
    const k = L * Math.exp(-Math.pow(2*(s - P)/(W1*deg), 2)) * Math.exp(-Math.pow(2*fTheta/W2, 2));
    const R = Re + k;

    const cotAlpha = 1 / Math.tan(alpha);
    const growth = Math.exp(theta * cotAlpha);

    const sPhi = s + phi;
    const cosSPhi = Math.cos(sPhi), sinSPhi = Math.sin(sPhi);
    const cosTheta = Math.cos(theta), sinTheta = Math.sin(theta);
    const cosThetaOmega = Math.cos(theta + omega), sinThetaOmega = Math.sin(theta + omega);
    const sinBeta = Math.sin(beta), cosBeta = Math.cos(beta);
    const sinMu = Math.sin(mu), cosMu = Math.cos(mu);

    const x = D * (A * sinBeta * cosTheta + R * cosSPhi * cosThetaOmega - R * sinMu * sinSPhi * sinTheta) * growth;
    const y = (-A * sinBeta * sinTheta - R * cosSPhi * sinThetaOmega - R * sinMu * sinSPhi * cosTheta) * growth;
    const z = (-A * cosBeta + R * sinSPhi * cosMu) * growth;

    const stripeVal = input.stripes > 0 ? Math.pow(Math.sin(theta * input.stripes), 2) * input.stripeInt : 0;
    const noduleVal = L > 0 ? (k / L) * 0.2 : 0;

    const hue = (input.hueBase + (theta / (turns * 2 * Math.PI)) * input.hueSpread + stripeVal * 0.1) % 1;
    const sat = Math.min(1, input.saturation + stripeVal * 0.2);
    const val = Math.min(1, input.brightness + noduleVal - stripeVal * 0.15);

    const h = hue * 6, i = Math.floor(h), f = h - i;
    const p = val * (1 - sat), q = val * (1 - sat * f), t = val * (1 - sat * (1 - f));
    let r, g, bl;
    switch (i % 6) {
        case 0: r = val; g = t; bl = p; break;
        case 1: r = q; g = val; bl = p; break;
        case 2: r = p; g = val; bl = t; break;
        case 3: r = p; g = q; bl = val; break;
        case 4: r = t; g = p; bl = val; break;
        case 5: r = val; g = p; bl = q; break;
    }

    return { x, y, z, r, g, b: bl, a: 1 };
}
