// the function "surface" defines a colored parametric surface, this one is a sphere
// the function takes an input object, which always has a "u" and "v" value
// input may also have arbitrary parameters, like t, that are defined in the UI above

// the function returns an object with values {x,y,z,r,g,b,a}
// xyz defines coordinates, rgb defines color, a defines transparency
// z is up
// rgba values are clipped between 0-1
// all values default to 1 if not explicitly returned

// both the "Math" and "math" libraries are available, 
// allowing complex mathematical operations

function surface(input) {
    const u = input.u;
    const v = input.v;
    const radius = input.radius;

    const x = Math.cos(u)*Math.sin(v)*radius;
    const y = Math.cos(u)*Math.cos(v)*radius;
    const z = Math.sin(u)*radius;

    const r = (x/radius) * 0.5 + 0.5;
    const g = (y/radius) * 0.5 + 0.5;
    const b = (z/radius) * 0.5 + 0.5;
    const a = 1.0;

    return { x, y, z, r, g, b, a };
 }
